select metprogram.titlos||'#'||metstuprog.dateanag||'#'||met.epvn||'#'||met.onoma||'#'||met.patr||'#'||met.tel1||'#'||met.tel2||'#'||met.email||'#'||met.perioxi||'#'||met.odos||'#'||met.tt||'#'||@round(totbau,2) from (met inner join metstuprog on met.mitroo = metstuprog.mitroo) inner join metprogram on metstuprog.progcode = metprogram.progcode where dateanag is not null order by titlos, metstuprog.dateanag desc;
